//Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product?


#include<stdio.h>
#include <string.h>

int main8(){
	char sample[] = "";
	long i, j, len=13;
	long number;
	unsigned long long sum=1;
	unsigned long long result=0;
	//unsigned long long tmp = 9*4*7*6*5*4*5*6*8*2*8*4*8;
	printf("length is %d\n", strlen(sample));
	//printf("%llu", tmp);
	
	for(i=0 ; i < 1000-len ; i++){
		//number = sample[i] - '0';
		//printf("%d \n\n", number);

		//make sum
		for(j=0 ; j < len ; j++){
			number = sample[i+j] - '0';
			//printf("%ld ", number);
			sum = number * sum;
		}
		//printf(" sum is %ld \n", sum);

		//biggest sum is result
		if(result < sum){
			result = sum;
		}
		sum=1;
	}
	
	printf("biggest is %llu" , result);
	

	
	return 0;
}